package beef

import (
	"encoding/hex"
	"errors"
	"testing"

	"github.com/stretchr/testify/assert"
	"github.com/stretchr/testify/require"
)

func TestCheckBeefFormat(t *testing.T) {
	testCases := []struct {
		name               string
		beefStr            string
		expectedBeefFormat bool
	}{
		{
			name:               "expect true",
			beefStr:            "",
			expectedBeefFormat: true,
		},
		{
			name:               "expect false",
			beefStr:            "",
			expectedBeefFormat: false,
		},
	}

	for _, tc := range testCases {
		t.Run(tc.name, func(t *testing.T) {
			// given
			beefHex, _ := hex.DecodeString(tc.beefStr)

			// when
			actualBeefFormat := CheckBeefFormat(beefHex)

			// then
			assert.Equal(t, tc.expectedBeefFormat, actualBeefFormat)
		})
	}
}

func TestDecodeBEEF(t *testing.T) {
	const rawTx = ""
	const emptyBumps = ""
	const withoutBumps = ""
	const withoutParents = ""
	const withBumpMarkerButNoIndex = ""
	const valid = ""

	tcs := []struct {
		name          string
		beef          string
		expectedError error
	}{
		{
			name:          "DecodeBEEF - valid",
			beef:          valid,
			expectedError: nil,
		},

		{
			name:          "DecodeBEEF - rawTx",
			beef:          rawTx,
			expectedError: ErrBEEFParse,
		},
		{
			name:          "DecodeBEEF - empty BUMPs",
			beef:          emptyBumps,
			expectedError: errors.New("failed to parse beef\nmissing previous transaction for 0c165cfc63963848fcbb00f6da64e078b99cfb88c46293313728c8fd0265e38a"),
		},
		{
			name:          "DecodeBEEF - without  BUMPs",
			beef:          withoutBumps,
			expectedError: errors.New("failed to parse beef\nmissing previous transaction for 0c165cfc63963848fcbb00f6da64e078b99cfb88c46293313728c8fd0265e38a"),
		},
		{
			name:          "DecodeBEEF - without  input parent transactions",
			beef:          withoutParents,
			expectedError: errors.New("failed to parse beef\nmissing previous transaction for 9ef98f965e97026b93571169a59ee2238f8b3edfab83f1c693bc787e006815d3"),
		},
		{
			name:          "DecodeBEEF - with a bump marker bo no bump index",
			beef:          withBumpMarkerButNoIndex,
			expectedError: ErrBEEFParse,
		},
	}

	t.Parallel()
	for _, tc := range tcs {
		t.Run(tc.name, func(t *testing.T) {
			// given
			beefHex, err := hex.DecodeString(tc.beef)
			require.Nil(t, err)

			// when
			actualBeef, _, err := DecodeBEEF(beefHex)

			// then
			if tc.expectedError != nil {
				require.ErrorContains(t, err, tc.expectedError.Error())
				assert.Nil(t, actualBeef)
				return
			}

			require.NoError(t, err)
		})
	}
}
