const (
	validFullyMinedBeef = ""

	invalidBeefLowFee = ""

	invalidBeefInvalidScripts = ""
)

func TestBeefValidator(t *testing.T) {
	t.Parallel()

	testCases := []struct {
		name                     string
		beefStr                  string
		isValidRootErr           error
		verificationUnsuccessful bool

		expectedErr     error
		expectErrTx     bool
		expectedErrTxID string
	}{
		{
			name:            "valid fully mined beef",
			beefStr:         validFullyMinedBeef,
			expectedErr:     nil,
			expectErrTx:     false,
			expectedErrTxID: "",
		},
		{
			name:            "invalid beef - low fees",
			beefStr:         invalidBeefLowFee,
			expectedErr:     validation.NewError(errors.New("transaction fee of 0 sat is too low - minimum expected fee is 1 sat"), api.ErrStatusFees),
			expectErrTx:     true,
			expectedErrTxID: "8184849de6af441c7de088428073c2a9131b08f7d878d9f49c3faf6d941eb168",
		},
		{
			name:            "invalid beef - invalid scripts",
			beefStr:         invalidBeefInvalidScripts,
			expectedErr:     validation.NewError(goscript.NewScriptError(goscript.SCRIPT_ERR_EQUALVERIFY), api.ErrStatusUnlockingScripts),
			expectErrTx:     true,
			expectedErrTxID: "ea2924da32c47b9942cda5ad30d3c01610ca554ca3a9ca01b2ccfe72bf0667be",
		},
		{
			name:           "verification failed - requested timed out",
			beefStr:        validFullyMinedBeef,
			isValidRootErr: ErrRequestTimedOut,

			expectedErr:     validation.NewError(errors.Join(ErrBEEFVerificationTimedOut, ErrRequestTimedOut), api.ErrStatusBeefValidationMerkleRoots),
			expectErrTx:     false,
			expectedErrTxID: "",
		},
		{
			name:           "verification failed - request failed",
			beefStr:        validFullyMinedBeef,
			isValidRootErr: ErrRequestFailed,

			expectedErr:     validation.NewError(errors.Join(ErrBEEFVerificationFailed, ErrRequestFailed), api.ErrStatusBeefValidationMerkleRoots),
			expectErrTx:     false,
			expectedErrTxID: "",
		},
		{
			name:           "verification failed - other error",
			beefStr:        validFullyMinedBeef,
			isValidRootErr: errors.New("other error"),

			expectedErr:     validation.NewError(errors.New("other error"), api.ErrStatusBeefValidationMerkleRoots),
			expectErrTx:     false,
			expectedErrTxID: "",
		},
		{
			name:                     "verification unsuccessful",
			beefStr:                  validFullyMinedBeef,
			isValidRootErr:           nil,
			verificationUnsuccessful: true,

			expectedErr:     validation.NewError(ErrBEEFVerificationFailed, api.ErrStatusBeefValidationFailedBeefInvalid),
			expectErrTx:     false,
			expectedErrTxID: "",
		},
	}

	for _, tc := range testCases {
		t.Run(tc.name, func(t *testing.T) {
			// given
			beefHex, err := hex.DecodeString(tc.beefStr)
			require.NoError(t, err)

			beefTx, _, err := beef.DecodeBEEF(beefHex)
			require.NoError(t, err)

			ctMock := &mocks.ChainTrackerMock{
				IsValidRootForHeightFunc: func(_ context.Context, _ *chainhash.Hash, _ uint32) (bool, error) {
					return !tc.verificationUnsuccessful, tc.isValidRootErr
				},
				CurrentHeightFunc: func(_ context.Context) (uint32, error) {
					return 1, nil
				},
			}

			se := goscript.NewScriptEngine("regtest")
			sut := New(getPolicy(1), ctMock, se, int32(10000))

			// when
			actualTx, err := sut.ValidateTransaction(context.TODO(), beefTx, validation.StandardFeeValidation, validation.StandardScriptValidation, 632099)

			// then
			if tc.expectedErr != nil {
				require.Equal(t, tc.expectedErr, err)

				if tc.expectErrTx {
					assert.NotNil(t, actualTx)
					assert.Equal(t, tc.expectedErrTxID, actualTx.TxID().String())
				} else {
					assert.Nil(t, actualTx)
				}
				return
			}

			require.NoError(t, err)
		})
	}
}

func TestValidateScripts(t *testing.T) {
	testCases := []struct {
		name          string
		beefStr       string
		expectedError *validation.Error
	}{
		{
			name:          "Valid Full Mined Beef",
			beefStr:       "",
			expectedError: nil,
		},
		{
			name:          "Valid Not Mined Beef",
			beefStr:       "",
			expectedError: nil,
		},
		{
			name:          "Invalid Beef - Invalid Scripts",
			beefStr:       "",
			expectedError: validation.NewError(goscript.NewScriptError(goscript.SCRIPT_ERR_EQUALVERIFY), api.ErrStatusUnlockingScripts),
		},
	}

	for _, tc := range testCases {
		t.Run(tc.name, func(t *testing.T) {
			// given
			beefHex, err := hex.DecodeString(tc.beefStr)
			require.NoError(t, err)

			beefTx, txID, err := beef.DecodeBEEF(beefHex)
			require.NoError(t, err)

			se := goscript.NewScriptEngine("regtest")
			// when
			for _, btx := range beefTx.Transactions {
				if btx.Transaction.TxID().String() != txID {
					continue
				}

				actualError := validateScripts(btx, se, int32(10000), int32(10000))
				if tc.expectedError != nil {
					require.Equal(t, tc.expectedError, actualError)
					return
				}

				require.Nil(t, actualError)
			}
		})
	}
}

func getPolicy(satoshisPerKB uint64) *bitcoin.Settings {
	var policy *bitcoin.Settings

	_ = json.Unmarshal([]byte(testdata.DefaultPolicy), &policy)

	policy.MinMiningTxFee = float64(satoshisPerKB) / 1e8
	return policy
}

func TestCumulativeCheckFees(t *testing.T) {
	tcs := []struct {
		name           string
		beefHex        string
		feeModel       *feemodel.SatoshisPerKilobyte
		ancestorsLimit int

		expectedError *validation.Error
	}{
		{
			name:    "mined parents only - valid fee",
			beefHex: "",
			feeModel: func() *feemodel.SatoshisPerKilobyte {
				return &feemodel.SatoshisPerKilobyte{Satoshis: 1}
			}(),
		},
		{
			name:    "mined parents only - to low fee",
			beefHex: "",
			feeModel: func() *feemodel.SatoshisPerKilobyte {
				return &feemodel.SatoshisPerKilobyte{Satoshis: 2}
			}(),
			expectedError: validation.NewError(errors.New("cumulative transaction fee of 1 sat is too low - minimum expected fee is 2 sat"), api.ErrStatusCumulativeFees),
		},
		{
			name:    "cumulative (at least one unmined parent) fees too low",
			beefHex: "",
			feeModel: func() *feemodel.SatoshisPerKilobyte {
				return &feemodel.SatoshisPerKilobyte{Satoshis: 5}
			}(),
			expectedError: validation.NewError(errors.New("cumulative transaction fee of 5 sat is too low - minimum expected fee is 25 sat"), api.ErrStatusCumulativeFees),
		},
		{
			name:    "cumulative (at least one unmined parent) fees sufficient",
			beefHex: "",
			feeModel: func() *feemodel.SatoshisPerKilobyte {
				return &feemodel.SatoshisPerKilobyte{Satoshis: 1}
			}(),
		},
	}

	for _, tc := range tcs {
		t.Run(tc.name, func(t *testing.T) {
			// given
			bytes, _ := hex.DecodeString(tc.beefHex)
			beefTx, _, err := beef.DecodeBEEF(bytes)
			require.NoError(t, err)

			// when
			actualError := cumulativeCheckFees(beefTx, tc.feeModel)

			// then
			if tc.expectedError == nil {
				require.Nil(t, actualError)
			} else {
				require.NotNil(t, actualError)
				assert.Equal(t, actualError.Err.Error(), tc.expectedError.Err.Error())
				assert.Equal(t, actualError.ArcErrorStatus, tc.expectedError.ArcErrorStatus)
			}
		})
	}
}
